
DROP TABLE IF EXISTS `hw_hdds_db_source`;
CREATE TABLE `hw_hdds_db_source` (
  `Code` varchar(50) NOT NULL,
  `Desc` varchar(200) DEFAULT NULL COMMENT '描述',
  `Type` varchar(100) DEFAULT NULL COMMENT '数据库类型，当前仅支持mysql',
  `Host` varchar(50) DEFAULT NULL COMMENT '主机',
  `Port` varchar(10) DEFAULT NULL COMMENT '端口',
  `Database` varchar(100) DEFAULT NULL COMMENT '数据库名字',
  `UserName` varchar(100) DEFAULT NULL COMMENT '用户名',
  `Password` varchar(100) DEFAULT NULL COMMENT '密码',
  `PoolMax` int(11) DEFAULT NULL COMMENT '连接池最大数量',
  `PoolMin` int(11) DEFAULT NULL COMMENT '连接池最小数量',
  `DriverClassName` varchar(200) DEFAULT NULL COMMENT '驱动类',
  PRIMARY KEY (`Code`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='数据源';




DROP TABLE IF EXISTS `hw_hdds_grab_rule`;
CREATE TABLE `hw_hdds_grab_rule` (
  `Code` varchar(50) NOT NULL COMMENT '规则编号',
  `Desc` varchar(200) DEFAULT NULL COMMENT '描述',
  `Rule` varchar(5000) DEFAULT NULL COMMENT '规则内容',
  `IsDistribute` tinyint(1) DEFAULT NULL COMMENT '是否分发',
  `DistributField` varchar(50) DEFAULT 'StoreID' COMMENT '分发数据区分字段',
  `InspectField` varchar(50) DEFAULT 'LastUpdateTime' COMMENT '抓取条件字段',
  `LastProcessTime` timestamp(6) NULL DEFAULT NULL COMMENT '最后执行时间',
  `DBSource` varchar(50) DEFAULT NULL COMMENT '数据源',
  `IsLock` tinyint(1) DEFAULT NULL COMMENT '是否被锁定',
  `RouteKey` varchar(50) DEFAULT NULL COMMENT 'MQ Route KEY',
  `SpecialMethod` varchar(100) DEFAULT NULL COMMENT '需要进行特殊处理的方法',
  PRIMARY KEY (`Code`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='数据采集规则';




DROP TABLE IF EXISTS `hw_hdds_write_rule`;
CREATE TABLE `hw_hdds_write_rule` (
  `Code` varchar(50) NOT NULL COMMENT '编号',
  `Desc` varchar(200) DEFAULT NULL COMMENT '描述',
  `DBSource` varchar(50) DEFAULT NULL COMMENT '数据源',
  `Rule` varchar(5000) DEFAULT NULL COMMENT '规则',
  `InspectField` varchar(100) DEFAULT 'ModifyDate' COMMENT '条件字段',
  `BeforeMethod` varchar(100) DEFAULT NULL COMMENT '在将数据写入数据库之前调用的方法',
  `AfterMethod` varchar(100) DEFAULT NULL COMMENT '在将数据写入数据库之后调用的方法',
  PRIMARY KEY (`Code`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='数据回写规则，注意如果要重置接收到数据的更新时间，配置规则mapping的时候去掉时间字段的对应即可。';


